import { shallowMount, RouterLinkStub } from '@vue/test-utils';
import CoursePreview from '@/components/CoursePreview';

test('render course description props', () => {
	const description = "This is a test description";

	const data = {
		description: description,
		url: "/users/1/courses/1",
		title: 'Course Title',
		cover: 'data:image/jpeg;base64,',
	};

	const wrapper = shallowMount(CoursePreview, {
		propsData: data,
		stubs: {
			RouterLink: RouterLinkStub
		}
	});

	let link = wrapper.findComponent(RouterLinkStub);
	let img = wrapper.find('img');

	expect(wrapper.text()).toContain(description);
	expect(img.attributes().src).toBe(data.cover);
	expect(link.text()).toBe(data.title);
	expect(link.props().to).toBe(data.url);
})